h'])) {                            $search = trim($_POST['search']);                        }                        if (!empty($search)) {                            foreach ($list as $obj) {                                $active = false;                                if (mb_strpos(mb_strtolower($obj->post_title), mb_strtolower($search)) === false ) {                                    if (mb_strpos(mb_strtolower(get_field('description', $obj->ID)), mb_strtolower($search)) === false ) {                                    } else {                                        $active = true;                                    }                                } else {                                    $active = true;                                }                                if ($active) {                                    $found[] = $obj;                                    if (count($found) > 25) {                                        break;                                    }                                }                            }                        }                        foreach ($found as $index=>$obj) { ?>                            <div class="col-md-6" <?php                                if (!($index % 2)) {                                    echo 'style="clear: both;"';                                }                                ?>>                                <a class="news_mini_a" href="/<?php echo $table; ?>/<?php echo $obj->post_name; ?>">                                    <div class="news_mini">                                        <div class="news_mini_date">                                            <?php                                            $date = strtotime($obj->post_date);                                            $date = date("d ",$date) . get_r_month(date("n",$date)) . date(" Y H:i",$date);                     