<?phprequire_once($_SERVER['DOCUMENT_ROOT'] . '/wp-load.php');global $wpdb;include 'Classes/PHPExcel.php';$objPHPExcel = new PHPExcel();$objPHPExcel->setActiveSheetIndex(0);$active_sheet = $objPHPExcel->getActiveSheet();$active_sheet->setTitle("Добровольцы");$objPHPExcel->getDefaultStyle()->getFont()->setName('Arial');$objPHPExcel->getDefaultStyle()->getFont()->setSize(8);$active_sheet->getColumnDimension('A')->setWidth(30);$active_sheet->getColumnDimension('B')->setWidth(30);$active_sheet->getColumnDimension('C')->setWidth(30);$active_sheet->getColumnDimension('D')->setWidth(30);$active_sheet->getColumnDimension('E')->setWidth(30);$active_sheet->getColumnDimension('F')->setWidth(30);$active_sheet->getColumnDimension('G')->setWidth(30);$active_sheet->setCellValue('A1', 'ФИО');$active_sheet->setCellValue('B1', 'Дата рождения');$active_sheet->setCellValue('C1', 'Пол');$active_sheet->setCellValue('D1', 'Город');$active_sheet->setCellValue('E1', 'Телефон');$active_sheet->setCellValue('F1', 'E-mail');$active_sheet->setCellValue('G1', 'Исследование');$active_sheet->setCellValue('H1', 'Дата регистрации');$volunteers = get_posts(    array(        'numberposts' => -1,        'offset' => 0,        'category' => '',        'include' => '',        'exclude' => '',        'meta_key' => '',        'meta_value' => '',        'post_type' => 'volunteers',        'post_parent' => '',        'post_status' => 'publish'    ));foreach ($volunteers as $index => $obj) {    $active_sheet->setCellValue('A' . ($index + 2), $obj->post_title);    $active_sheet->setCellValue('B' . ($index + 2), get_field('birth', $obj->ID));    $active_sheet->setCellValue('C' . ($index + 2), get_field('sex', $obj->ID));    $active_sheet->setCellValue('D' . ($index + 2), get_field('town', $obj->ID));    $active_sheet->setCellValue('E' . ($index + 2), get_field('phone', $obj->ID));    $active_sheet->setCellValue('F' . ($index + 2), get_field('email', $obj->ID));    $active_sheet->setCellValue('G' . ($index + 2), get_field('study', $obj->ID));    $active_sheet->setCellValue('H' . ($index + 2), $obj->post_date);}header("Content-Type:application/vnd.ms-excel");header("Content-Disposition:attachment;filename='volunteers.xls'");$objWriter = PHPExcel_IOFactory::createWriter($objPHPExcel, 'Excel5');$objWriter->save('php://output');exit();